using Npc.Foundation.Helper;
using Npc.Foundation.Logger;
using NpcCore.Wpf.Helpers;
using System;
using System.IO;
using System.Windows.Media.Imaging;

namespace NpcCore.Wpf.Core
{
    internal static class PalletImageMaker
    {
        private static PixelColor[,] _palletImage = null;
        internal static PixelColor[,] PalletImage
        {
            get
            {
                if (_palletImage == null)
                {
                    // BitmapImage bitmap = new BitmapImage(new Uri("Resources/Pallet.jpg", UriKind.Relative));

                    byte[] ImageByte = Convert.FromBase64String("");

                    BitmapImage bitmap = ByteToBitmapImage(ImageByte);

                    // [NCS-2695] CID 171138 Dereference null return value
                    //var pixels = bitmap.CopyPixels();
                    //_palletImage = pixels.Slice(0, 0, bitmap.PixelHeight - 1, 1);
                    var pixels = bitmap?.CopyPixels();
                    _palletImage = pixels?.Slice(0, 0, bitmap.PixelHeight - 1, 1);
                }

                return _palletImage;
            }
        }

        public static BitmapImage ByteToBitmapImage(byte[] imageData)
        {
            if (imageData == null || imageData.Length <= 0)
                return null;

            var image = new BitmapImage();
            using (var mem = new MemoryStream(imageData))
            {
                mem.Position = 0;
                image.BeginInit();
                image.CreateOptions = BitmapCreateOptions.PreservePixelFormat;
                image.CacheOption = BitmapCacheOption.OnLoad;
                image.UriSource = null;
                image.StreamSource = mem;
                image.EndInit();
            }
            image.Freeze();

            return image;
        }

        /// <summary>
        /// Defines the Pallet Image.
        /// </summary>
        /// <param name="maxValue">The maximum value.</param>
        /// <param name="palletMaxHeight">Maximum height of the pallet.</param>
        /// <returns></returns>
        internal static WriteableBitmap DefineImage(double maxValue, double palletMaxHeight = -1)
        {
            WriteableBitmap wbitmap = null;
            try
            {
                if (maxValue <= 0)
                    return wbitmap;
                if (palletMaxHeight == -1)
                    palletMaxHeight = maxValue;
                else if (palletMaxHeight <= 0)
                    palletMaxHeight = 0;
                else if (palletMaxHeight > maxValue)
                    palletMaxHeight = maxValue;
                palletMaxHeight += EnumHelper.TOLERANCE;
                BitmapPixelMaker bmMaker = null;
                if (maxValue <= 256
                    && palletMaxHeight == maxValue)
                {
                    bmMaker = new BitmapPixelMaker(PalletImage);
                }
                else
                {
                    bmMaker = DefineBitmapPixel(maxValue, palletMaxHeight);
                }
                // Convert the BitmapPixelMaker into a WriteableBitmap.
                wbitmap = bmMaker.MakeBitmap(96, 96);
                // Save the bitmap into a file.
                //wbitmap.Save("Texture.png");
            }
            catch (Exception ex)
            {
                LogHelper.Fatal(ex);
            }

            return wbitmap;
        }

        private static BitmapPixelMaker DefineBitmapPixel(double maxValue, double palletMaxHeight)
        {
            int pixelWidth = PalletImage.GetLength(1);
            int pixelHeight = (palletMaxHeight / maxValue) > 0.2 ? 2048 : 10240;
            int palletHeight = PalletImage.GetLength(0) - 1;

            double textureScale = pixelHeight * ((maxValue - palletMaxHeight) / maxValue);

            // Make the BitmapPixelMaker.
            var bmMaker = new BitmapPixelMaker(pixelWidth, pixelHeight);

            // Set the pixel colors.
            PixelColor pixel;
            for (int iz = 0; iz < pixelHeight; iz++)
            {
                int h = 0;
                if (palletMaxHeight == 0)
                {
                    h = 0;
                }
                else if (iz == pixelHeight - 1)
                {
                    h = palletHeight;
                }
                else if (iz <= textureScale)
                {
                    h = 0;
                }
                else
                {
                    // Convert into a value between 1 and 255.
                    h = (int)(palletHeight * (iz - textureScale) / (pixelHeight - textureScale));
                    if (h < 1)
                        h = 1;
                    else if (h > palletHeight)
                        h = palletHeight;
                }

                pixel = PalletImage[h, 0];
                for (int ix = 0; ix < pixelWidth; ix++)
                {
                    bmMaker.SetPixel(ix, iz, pixel.Red, pixel.Green, pixel.Blue, pixel.Alpha);
                }
            }

            return bmMaker;
        }


        /// <summary>
        /// Save the WriteableBitmap into a PNG file.
        /// </summary>
        /// <param name="wbitmap">The wbitmap.</param>
        /// <param name="filename">The filename.</param>
        [System.Diagnostics.CodeAnalysis.SuppressMessage("Major Code Smell", "S1144:Unused private types or members should be removed", Justification = "<Pending>")]
        private static void Save(this WriteableBitmap wbitmap, string filename)
        {
            // Save the bitmap into a file.
            using (FileStream stream = new FileStream(filename, FileMode.Create))
            {
                PngBitmapEncoder encoder = new PngBitmapEncoder();
                encoder.Frames.Add(BitmapFrame.Create(wbitmap));
                encoder.Save(stream);
            }
        }
    }
}
